import Vue from 'vue'
import Router from 'vue-router'
import Meta from 'vue-meta'

import './style.css'

Vue.use(Router)
Vue.use(Meta)

export default new Router({
  mode: 'history',
  routes: [
    {
      path: '/page-03',
      name: 'Page-03',
      component: () => import('@/views/page-03.vue'),
      meta: {
        requiresAuth: true
      }
    },
    {
      path: '/sign-in',
      name: 'Sign In',
      component: () => import('@/views/page-02.vue')
    },
    {
      path: '/',
      name: 'Home',
      component: () => import('@/views/home.vue')
    },
    {
      path: '/dashboard',
      name: 'Dashboard',
      component: () => import('@/views/page-03.vue'),
      meta: {
        requiresAuth: true
      }
    },
    {
      path: '*',
      name: 'Not Found',
      component: () => import('@/views/not-found.vue')
    }
  ]
})

// Navigation guard
Router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('userEmail') // Simple auth check
  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isAuthenticated) {
      next({
        path: '/sign-in',
        query: { redirect: to.fullPath }
      })
    } else {
      next()
    }
  } else {
    next()
  }
})
